try {
    const originalTimestamp = msg.payload.timestamp;

    // Guardar el ID del contenedor ANTES de modificar el payload
    const containerId = msg.payload.id || msg.payload.id_contenedor || "unknown";

    // Preparar los fields (datos num√©ricos)
    const fields = {
        nivel_cm: Number(parseFloat(msg.payload.nivel_cm)) || 0,
        temperatura_c: Number(parseFloat(msg.payload.temperatura_c)) || 0,
        volumen_L: Number(parseFloat(msg.payload.volumen_L)) || 0,
        caudal_Lps: Number(parseFloat(msg.payload.caudal_Lps)) || 0,
        ETA_segundos: Number(parseFloat(msg.payload.ETA_segundos)) || 0
    };

    // Preparar los tags (metadatos para filtrado)
    const tags = {
        id_contenedor: String(containerId)
    };

    // Estructura correcta para el nodo influxdb out: [fields, tags]
    msg.payload = [fields, tags];

    // Timestamp correcto
    msg.timestamp = new Date(originalTimestamp || Date.now());

    node.log("Fields: " + JSON.stringify(fields));
    node.log("Tags: " + JSON.stringify(tags));
    node.log("Container ID: " + containerId);

    return msg;

} catch (error) {
    node.error("Error: " + error.message);
    return null;
}
